    // dataManuallyChanged.value = true;
    const newItemIndex = _map(data, 'id').indexOf(id);
    initialIndex.value = newItemIndex;
    currIndex.value = newItemIndex;
    translateX.value = 0;
    translateY.value = 0;
  }, [data]);

